mapper.select:
select no, title, name, regdate, readcount, content
from article
where no = #{no}

select no ,title, password, name, regdate, readcount, content
from article
where no = #{no}

mapper.update:
		update ARTICLE
		set name = #{name},
		title = #{title}, CONTENT= #{content}
where no = #{no} and PASSWORD = #{password}

mapper.delete:
DELETE article
WHERE no = #{no}
AND password = #{password}

mapper.update:
UPDATE article
set readcount = article.readcount +1
where no = #{no}
